--------------------------------------------------------------------------------- XLS Reader Test---- A simple test fixture for xlsreader module--local xls = require "XlsReader"l_workbookName = "c:/lua/misc_experiments/xls_reader_tests/testdata.xlsx"l_worksheetName = "FirstSheet"l_fieldNames = {	"State Name",	"State Code",	"Rate",	"Effective Date"}print("This is a test fixture for the XLS Reader Module")print("\n")print("Ok lets load and read that XLS worksheet!")xls.SetShowDebugMessages(true)xls.SetMaxRows(100)xls.AddFieldNames(l_fieldNames)local buffer = xls.ReadXlsFile(l_workbookName, l_worksheetName)print("Lets see what we loaded .. ")local i = 1for _,rec in pairs(buffer) do	for k,v in pairs(rec) do		print("I = " .. i .. " K = " .. k .. " V = " .. v)	end	i = i + 1endprint("That's all folks .. ")